import React, { useState } from "react";
import { Form, Card, Button, Image } from "react-bootstrap";
import validator from "validator";
import pic1 from "../../assets/pic1.jpg";
import pic2 from "../../assets/pic2.jpg";
import pic3 from "../../assets/pic3.jpg";
import ButtonGroup from "react-bootstrap/ButtonGroup";
import ToggleButton from "react-bootstrap/ToggleButton";
const StepOne = ({ nextStep, handleFormData, values, prevStep }) => {
  const [error, setError] = useState(false);

  const submitFormData = (e) => {
    e.preventDefault();

    if (
      validator.isEmpty(values.vehicleSelect) ||
      validator.isEmpty(values.maxPrice)
    ) {
      setError(true);
    } else {
      nextStep();
    }
  };

  return (
    <div className="vehicle-select">
      <div>
        <Form onSubmit={submitFormData}>
          {/* toggle buttons  */}
          <div className="vehicles">
            <div className="vehicle-card">
              <img src={pic1} alt="" />
              <p>Honda Civic 2020</p>
              <input
                className=""
                name="radio1"
                type="radio"
                defaultValue={values.vehicleSelect}
                value="Honda Civic 2020"
                onChange={(e) => {
                  alert("Honda Civic 2020 Selected");
                  handleFormData("vehicleSelect");
                }}
              />
            </div>
            <div className="vehicle-card">
              <img src={pic2} alt="" />
              <p>Ford F150 2014</p>
              <input
                className=""
                name="radio2"
                type="radio"
                value="Ford F150 2014"
                onChange={(e) => {
                  alert("Honda Civic 2020 Selected");
                  handleFormData("firstName");
                }}
              />
            </div>
            <div className="vehicle-card">
              <img src={pic3} alt="" />
              <p>Nissan Murano 2011</p>
              <input
                className=""
                name="radio3"
                type="radio"
                value="Nissan Murano 2011"
                onChange={(e) => {
                  alert("Honda Civic 2020 Selected");
                  handleFormData("firstName");
                }}
              />
            </div>
          </div>
          {/* max price */}
          <Form.Group className="mb-3">
            <Form.Label>Maximum Bidding Price</Form.Label>
            <Form.Control
              style={{ border: error ? "2px solid red" : "" }}
              name="maxPrice"
              defaultValue={values.maxPrice}
              type="number"
              placeholder="Maximum Price"
              onChange={handleFormData("maxPrice")}
            />
            {error ? (
              <Form.Text style={{ color: "red" }}>
                This is a required field
              </Form.Text>
            ) : (
              ""
            )}
          </Form.Group>
          <div />

          {/* <Form.Group className="mb-3">
            <Form.Label>First Name</Form.Label>
            <Form.Control
              style={{ border: error ? "2px solid red" : "" }}
              name="firstName"
              defaultValue={values.firstName}
              type="text"
              placeholder="First Name"
              onChange={handleFormData("firstName")}
            />
            {error ? (
              <Form.Text style={{ color: "red" }}>
                This is a required field
              </Form.Text>
            ) : (
              ""
            )}
          </Form.Group> */}

          <Button variant="primary" type="submit">
            Continue
          </Button>
        </Form>
      </div>
    </div>
  );
};

export default StepOne;
