import React from "react";
import { Container, Button } from "react-bootstrap";

const Submission = ({ values, nextStep }) => {
  const { firstName, lastName, email, vehicleSelect, maxPrice } = values;
  const handleClick = (e) => {
    e.preventDefault();
    nextStep();
  };
  return (
    <>
      <h1 className="mb-5">Please verify your order:</h1>

      <p>
        <b>First Name: </b>
        {firstName}
      </p>
      <p>
        <b>Last Name:</b> {lastName}
      </p>
      <p>
        <b>Email:</b> {email}
      </p>
      <p>
        <b>Selected Vehicle:</b> {vehicleSelect}
      </p>
      <p>
        <b>Selected Bid Price:</b> {maxPrice}$
      </p>

      <Button className="mt-5" onClick={handleClick} variant="primary">
        Confirm Details
      </Button>
    </>
  );
};

export default Submission;
