import React, { useState } from "react";
import { Form, Card, Button, Image } from "react-bootstrap";
import validator from "validator";
import pic1 from "../../assets/pic1.jpg";
import pic2 from "../../assets/pic2.jpg";
import pic3 from "../../assets/pic3.jpg";
import ToggleButton from "react-bootstrap/ToggleButton";
import ToggleButtonGroup from "react-bootstrap/ToggleButtonGroup";

const StepOne = ({ nextStep, handleFormData, values, prevStep }) => {
  const [error, setError] = useState(false);

  const submitFormData = (e) => {
    e.preventDefault();
    nextStep();
  };

  return (
    <div className="">
      <div>
        <Form onSubmit={submitFormData}>
          <ToggleButtonGroup
            type="radio"
            name="options"
            defaultValue={"Honda Civic 2020"}
          >
            <ToggleButton
              id="tbg-radio-1"
              value={"Honda Civic 2020"}
              defaultValue={values.vehicleSelect}
              type="radio"
              onChange={handleFormData("vehicleSelect")}
            >
              <Image src={pic1} responsive />
              radio1
            </ToggleButton>

            <ToggleButton
              id="tbg-radio-2"
              value={"Ford F150 2017"}
              defaultValue={values.vehicleSelect}
              type="radio"
              onChange={handleFormData("vehicleSelect")}
            >
              <Image src={pic2} responsive />
              Radio 2
            </ToggleButton>

            <ToggleButton
              id="tbg-radio-3"
              value={"Nissan Murano 2011"}
              defaultValue={values.vehicleSelect}
              type="radio"
              onChange={handleFormData("vehicleSelect")}
            >
              <Image src={pic3} responsive />
              Radio 3
            </ToggleButton>
          </ToggleButtonGroup>

          <div />
          {/* maxprice */}

          <Form.Group className="mb-3">
            <Form.Label>Last Name</Form.Label>
            <Form.Control
              style={{ border: error ? "2px solid red" : "" }}
              name="lastName"
              defaultValue={values.lastName}
              type="text"
              placeholder="Last Name"
              onChange={handleFormData("lastName")}
            />
            {error ? (
              <Form.Text style={{ color: "red" }}>
                This is a required field
              </Form.Text>
            ) : (
              ""
            )}
          </Form.Group>
          {/* macxprice end */}
          <Button variant="primary" type="submit">
            Continue
          </Button>
        </Form>
      </div>
    </div>
  );
};

export default StepOne;
